/* Personnalisation de la barre de défilement pour les petits écrans */
@media (max-width: 640px) {
    .sm\:scrollbar-thin::-webkit-scrollbar {
        width: 1px; /* Taille de la barre de défilement */
    }

    .sm\:scrollbar-thin::-webkit-scrollbar-thumb {
        background-color: #6B7280; /* Couleur de la barre de défilement */
        border-radius: 10px;
    }

    .sm\:scrollbar-thin::-webkit-scrollbar-track {
        background-color: #E5E7EB; /* Couleur de fond du track */
    }
}








